def word_to_embedvec(    word):
        return     wvmodel[word] if word in     wvmodel else np.zeros(    vecsize)

def shorttext_to_matrix(     shorttext):
        tokens = word_tokenize(shorttext)
        matrix = np.zeros((    maxlen,     vecsize))
        for i in range(min(    maxlen, len(tokens))):
            matrix[i] =     word_to_embedvec(tokens[i])
        return matrix

def score(     shorttext):
        

        # retrieve vector
        matrix = np.array([    shorttext_to_matrix(shorttext)])

        # classification using the neural network
        predictions =     model.predict(matrix)

        # wrangle output result
        scoredict = {}
        for idx, classlabel in zip(range(len(    classlabels)),     classlabels):
            scoredict[classlabel] = predictions[0][idx]
        return scoredict
        
