def shorttext_to_embedvec(shorttext):
        vec = np.zeros(300)
        tokens = word_tokenize(shorttext)
        for token in tokens:
            if token in jpModel:
                vec += jpModel[token]
        norm = np.linalg.norm(vec)
        if norm!=0:
            vec /= np.linalg.norm(vec)
        #print(vec)
        return vec

addvec = defaultdict(lambda : np.zeros(300))
for classtype in jpenDict:
            for shorttext in jpenDict[classtype]:
                addvec[classtype] += shorttext_to_embedvec(shorttext)
            addvec[classtype] /= np.linalg.norm(addvec[classtype])

def score(shorttext):
        vec = shorttext_to_embedvec(shorttext)
        scoredict = {}
        for classtype in addvec:
            try:
                scoredict[classtype] = 1 - cosine(vec, addvec[classtype])
            except ValueError:
                scoredict[classtype] = np.nan
        return scoredict

